<div class="modal fade" id="select_image_modal" tabindex="-1" role="dialog" aria-labelledby="genre_add_modalTitle"
    aria-hidden="true">
    <div class="modal-dialog modal-dialog-centered" role="document">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title text-uppercase" id="genre_add_modalTitle">Crop </h5>
            </div>
            <div class="modal-body">
                <div class="src-pages-Novels-Setting-UploadBookCoverDialog-style-module__vertical_column--2It3Y pl24">
                    <img src="data:image/png;base64,"
                        alt="upload local file"
                        class="src-pages-Novels-Setting-UploadBookCoverDialog-style-module__icon--3j7mH">
                    <h4 class="text-uppercase card-title">upload local file</h4>
                    <p class="card-description">Press to upload book cover.<br>600*800px, no more than 5M, .JPG, .JPEG</p>
                </div>
                <input type="file" accept="image/*" hidden="hidden" id="select">                
            </div>
            <div class="modal-footer">
                <button type="button" class="btn btn-light btn-rounded btn-sm" data-dismiss="modal">Cancel</button>
                <button type="button" id="upload-cover"
                    class="btn btn-gradient-primary mr-2 btn-rounded btn-sm">Upload</button>
            </div>
        </div>
    </div>
</div>